/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */
import { Link } from "react-router-dom";

// Take in props data to construct the component
const CampusView = (props) => {
  const {campus, deleteCampus, editCampus, unenroll} = props;

  let studentInfo;
  if (campus.students.length === 0) {
    studentInfo = (<p>(No students currently enrolled in this college)</p>);
  } else {
    studentInfo = (<h3>Total Students: {campus.students.length}</h3>);
  }

  // Render a single Campus view with list of its students (if any)
  return (
    <div>
      <h1>{campus.name}</h1>
      <img src={campus.imageUrl} height="200" width="200" alt="college campus"/>
      <p><strong>Address:</strong> {campus.address}</p>
      <p><strong>Description:</strong> {campus.description}</p>
      <Link to={`/editcampus/${campus.id}`}>
        <button style={{backgroundColor: '#11153e', padding: '12px 10px', border: 'none', margin: '10px 10px', fontWeight: 'bold', fontSize: '15px', color: 'white'}} onClick={() => editCampus(campus)}>Edit Campus Information</button>
      </Link> 

      <Link to={'/campuses'}>
        <button style={{backgroundColor: 'red', padding: '12px 10px', border: 'none', margin: '10px 20px', fontWeight: 'bold', fontSize: '15px', color: 'white'}} onClick={() => editCampus(campus)}onClick={() => deleteCampus(campus.id)}>Delete Campus</button>
      </Link> 
      {studentInfo}
      {campus.students.map( student => {
        let name = student.firstname + " " + student.lastname;
        return (
          <div key={student.id}>
            <Link to={`/student/${student.id}`}>
              <h3>{name}</h3>
            </Link>        
            <button onClick={() => unenroll(student.id)}>Unenroll</button>
          </div>
        );
      })}
      <br/>
      <Link to={'/students'}>
        <button>Enroll New Student</button>
      </Link> 
      <br/><br/>
    </div>
  );
};

export default CampusView;
